import React from "react";
import { useForm } from "react-hook-form";

const NewUser = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
    watch,
    reset,
  } = useForm();

  const onSubmit = handleSubmit((data) => {
    console.log(data);
    reset();
    //fetch
  });

  return (
    <>
      <h2>Crear Usuario</h2>
      <form onSubmit={onSubmit}>
        <label htmlFor="nombre">Nombre</label>
        <input
          type="text"
          {...register("nombre", {
            required: {
              value: true,
              message: "El nombre es requerido",
            },
            minLength: {
              value: 2,
              message: "El nombre debe tener al menos 2 caracteres",
            },
            maxLength: {
              value: 20,
              message: "El nombre debe tener máximo 20 caracteres",
            },
          })}
        />
        {errors.nombre && <span>{errors.nombre.message}</span>}

        <label htmlFor="correo">Correo</label>
        <input
          type="email"
          {...register("correo", {
            required: {
              value: true,
              message: "El correo es requerido",
            },
            pattern: {
              value: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
              message: "Correo no válido",
            },
          })}
        />
        {errors.correo && <span>{errors.correo.message}</span>}

        <label htmlFor="password">Contraseña</label>
        <input
          type="password"
          {...register("password", {
            required: {
              value: true,
              message: "Contraseña requerida",
            },
            minLength: {
              value: 5,
              message: "La contrseña debe tener al menos 5 caracteres",
            },
          })}
        />
        {errors.password && <span>{errors.password.message}</span>}

        <label htmlFor="confirmarPassword">Confirmar Contraseña</label>
        <input
          type="password"
          {...register("confirmarPassword", {
            required: {
              value: true,
              message: "Necesitas confirmar tu contraseña",
            },
            validate: (value) =>
              value === watch("password") || "Las contraseñas no coinciden",
          })}
        />
        {errors.confirmarPassword && (
          <span>{errors.confirmarPassword.message}</span>
        )}

        <label htmlFor="terminos">Acepto términos y condiciones</label>
        <input
          type="checkbox"
          {...register("terminos", {
            required: {
              value: true,
              message: "Debe aceptar términos y condiciones para continuar",
            },
          })}
        />
        {errors.terminos && <span>{errors.terminos.message}</span>}

        <button type="submit">Enviar</button>
      </form>
    </>
  );
};

export default newUser;
